import React from "react";
import { Box, Typography, Grid, Button ,makeStyles } from "@material-ui/core";

const skills = ["Javascript", "React.js", "Node.js"];

const useStyles = makeStyles((theme)=>({
    wrapper:{
        border: "1px solid #e8e8e8",
        cursor: "pointer",
    },
    companyName: {
        fontSize: "13.5px",
        backgroundColor: theme.palette.primary.main,
        padding: theme.spacing(0.75),
        borderRadius: "5px",
        display: "inline-block",
        fontWeight:600,
    },
    skillChip: {
        margin: theme.spacing(0.5),
        padding: theme.spacing(0.75),
        fontSize: "14.5px",
        borderRadius: "5px",
        // transition: ".3s",

        fontWeight: 600,
        backgroundColor: theme.palette.secondary.main,
        color:"#fff",
    }
}))
export default (props) => {
    const classes = useStyles();
  return (
    <Box p={2} mt={1} className={classes.wrapper}>
      <Grid container alignItems="center">
        <Grid item xs>
          <Typography variant="subtitle1">Frontend Dev</Typography>
          <Typography className={classes.companyName} variant="subtitle1">Google</Typography>
        </Grid>
        <Grid item container xs>
          {skills.map((skill) => (
            <Grid key={skill} className={classes.skillChip} item>
              {skill}
            </Grid>
          ))}
        </Grid>
        <Grid item xs container direction="column" alignItems="flex-end" >
          <Grid item>
            <Typography variant="caption">
              2577 min ago | Full time | Remote
            </Typography>
          </Grid>
          <Grid item>
            <Box mt={2}>
              <Button variant="outlined">Check</Button>
            </Box>
          </Grid>Â 
        </Grid>
      </Grid>
    </Box>
  );
};
