import React from "react";
import { Box, CircularProgress, Grid, ThemeProvider } from "@material-ui/core";
import theme from "./theme/theme"
import Header from "./component/Header/Index"
import SearchBar from "./component/SearchBar"
import JobCards from "./component/jobs/jobCards";
import NewJobModal from "./component/jobs/NewJobModal";
// import jobData from './dummyData';
import { useState } from "react";
import {firestore} from './component/firebase/config'
import { useEffect } from "react";


export default () => {
  const [jobs, setjobs] = useState([]);
  const [loading, setloading] = useState(true);

  const fetchjobs = async () => {
    const req = await firestore.collection('job collection').orderBy('postedOn', 'desc').get();
    // console.log(req);
    const tempjobs = req.docs.map(job => ({ ...job.data(),id:job.id,postedOn:job.data().postedOn.toDate() }))
    // console.log(tempjobs);
    setjobs(tempjobs)
  }

  useEffect(() => {
    fetchjobs();
  },[])
  return (
    <ThemeProvider theme={theme}>
      <Header />
      <NewJobModal />
    <Grid container justify="center">
      <Grid item xs={10}>
          <SearchBar />

          {
            loading ? <CircularProgress />
              : jobs.map(job => <JobCards key={job.id} {...job} />)
          }



          {/* {jobs.map(job => <JobCards key={job.id} {...job} />)} */}
      </Grid>
    </Grid>
  </ThemeProvider>
)};
