import React from "react";
import { Box, Dialog, DialogActions, DialogContent, DialogTitle, IconButton, makeStyles, Typography } from '@material-ui/core'
import { Close as CloseIcon } from '@material-ui/icons'
import {format} from 'date-fns'

const useStyles = makeStyles({
    info: {
        "& > *": {
            margin:'5px'
        }
    }
})


export default (props) => {

    return(
    <Dialog open={!!Object.keys(props.job).length} fullWidth>
         <DialogTitle>
            <Box display="flex" justifyContent="space-between" alignItems="center">
               {props.job.title} @ {props.job.companyName}
                {/* onClick={closeModal} */}
          <IconButton >
            <CloseIcon />
          </IconButton>
        </Box>
        </DialogTitle>
        <DialogContent>
            <Box>
                <Box className={classes.info} display="flex">
                    <Typography variant="caption">Posted On :</Typography>
                    <Typography variant="caption">{props.job.postedOn  && format(props.job.postedOn,"dd/MMM/yyy HH:MM")}</Typography>
                </Box>
            </Box>
        </DialogContent>
        <DialogActions>

        </DialogActions>
    </Dialog>
)}
